-- Listage des données de la table playranker.comment : ~7 rows (environ)
INSERT INTO `comment` (`id`, `user_id`, `game_id`, `content`, `created_at`) VALUES
	(1, 15, 39, 'An epic RPG with an incredibly deep story, immersive world, and choices that truly impact the gameplay. A masterpiece in storytelling and open-world design.', '2024-10-08 15:52:35'),
	(2, 19, 40, '"A groundbreaking open-world experience, offering an unparalleled blend of storytelling, action, and freedom to explore a massive, living city.', '2024-10-11 15:07:36'),
	(3, 20, 41, 'A brilliant mix of mind-bending puzzles and witty dialogue, Portal 2 is a unique experience that challenges your thinking and keeps you laughing.', '2024-10-11 15:07:57'),
	(4, 21, 46, 'A fast-paced, team-oriented battle royale that stands out with its fluid movement, diverse characters, and exciting gameplay mechanics.', '2024-10-11 15:08:15'),
	(5, 15, 47, 'A highly tactical shooter that demands skill, precision, and teamwork, making it a cornerstone of competitive gaming for years.', '2024-10-11 15:08:36'),
	(6, 22, 48, 'An exhilarating co-op experience that lets you plan and execute daring heists with friends, filled with tension and teamwork.', '2024-10-11 15:08:50'),
	(7, 19, 49, '"A hauntingly beautiful platformer with a dark, minimalist atmosphere, Limbo draws you in with its eerie design and thoughtful puzzles.', '2024-10-11 15:34:56');

